export default {
  name: "Manage",
  data: function data() {
    return {
      data: [],
      api: process.env.VUE_APP_API_URL,
      ModalText: '在这里修改你想设置的图片名称',
      visible: false,
      confirmLoading: false,
      currentName: "default",
      styleobj: {
        height: "10px"
      }
    };
  },
  beforeCreate: function beforeCreate() {
    var _this = this;

    this.$http.get('/Pics/get_all_pics').then(function (response) {
      _this.data = response;
      console.log(response.status);
    });
  },
  methods: {
    edit_card: function edit_card(id, name) {
      this.visible = true;
      this.currentName = name;
    },
    handleOk: function handleOk(id) {
      var _this2 = this;

      console.log(id);
      this.ModalText = '在这里输入你想设置的图片名称：';
      this.confirmLoading = true;
      setTimeout(function () {
        _this2.visible = false;
        _this2.confirmLoading = false;

        _this2.$http.get('/Pics/edit_pic?id=' + id + "&name=" + _this2.currentName).then(function (response) {
          _this2.data = response;
          console.log(response.status);

          _this2.$http.get('/Pics/get_all_pics').then(function (response) {
            _this2.data = response;
            console.log(response.status);
          });
        });
      }, 2000);
    },
    handleCancel: function handleCancel(e) {
      var _this3 = this;

      this.$http.get('/Pics/get_all_pics').then(function (response) {
        _this3.data = response;
        console.log(response.status);
      });
      this.visible = false;
    },
    delete_card: function delete_card(e) {
      var _this4 = this;

      this.$http.get('/Pics/delete_one_pic?id=' + e).then(function (response) {
        _this4.data = response;
        console.log(response.status);

        _this4.$http.get('/Pics/get_all_pics').then(function (response) {
          _this4.data = response;
          console.log(response.status);
        });
      });
    },
    info: function info() {
      this.$message.info('Build For TechMan By Sunbelife');
    },
    handleChange: function handleChange(info) {
      var _this5 = this;

      var status = info.file.status;

      if (status !== 'uploading') {
        console.log(info.file, info.fileList);
      }

      if (status === 'done') {
        this.$http.get('/Pics/get_all_pics').then(function (response) {
          _this5.data = response;
          console.log(response.status);
        });
        this.$message.success("".concat(info.file.name, " \u6587\u4EF6\u4E0A\u4F20\u5B8C\u6BD5\u3002"));
      } else if (status === 'error') {
        this.$message.error("".concat(info.file.name, " \u6587\u4EF6\u4E0A\u4F20\u5931\u8D25\u3002"));
      }
    },
    go_to_show: function go_to_show() {
      this.$router.push({
        path: '/show'
      });
    }
  }
};
