export default {
    name: 'HelloWorld',
    data() {
        return {
            showText: ["Welcome", "TECHMAN | Magic Screen"],
            showTextCurIndex: -1,
            show: false,
            timer: 2000,
        }
    },
    beforeRouteEnter(to, from, next) {
        next(vm => {
            vm.$router.replace(from.path)
        })
    },
    mounted() {
        this.showFn();
    },
    methods: {
        showFn() {
            setTimeout(() => {
                this.showTextCurIndex++;
                this.show = true;
                setTimeout(() => {
                    this.$nextTick(() => {
                        this.show = false;
                    });
                    if (this.showTextCurIndex !== this.showText.length - 1) {
                        this.showFn();
                    } else {
                        this.$router.push({path:'/show'});
                    }
                }, this.timer);
            }, this.timer);
        }
    }
}
