export default {
    name: "Manage",
    data () {
        return {
            data: [],
            api: process.env.VUE_APP_API_URL,
            ModalText: '在这里修改你想设置的图片名称',
            visible: false,
            confirmLoading: false,
            currentName: "default",
            styleobj :{
                height: "10px",
            },
        }
    },
    beforeCreate () {
        this.$http.get('/Pics/get_all_pics')
            .then((response) => {
                this.data = response
                console.log(response.status)
            })
    },
    methods: {
        edit_card(id, name) {
            this.visible = true;
            this.currentName = name;
        },
        handleOk(id) {
            console.log(id)
            this.ModalText = '在这里输入你想设置的图片名称：';
            this.confirmLoading = true;
            setTimeout(() => {
                this.visible = false;
                this.confirmLoading = false;
                this.$http.get('/Pics/edit_pic?id=' + id + "&name=" + this.currentName)
                    .then((response) => {
                        this.data = response
                        console.log(response.status)
                        this.$http.get('/Pics/get_all_pics')
                            .then((response) => {
                                this.data = response
                                console.log(response.status)
                            })
                    })
            }, 2000);
        },
        handleCancel(e) {
            this.$http.get('/Pics/get_all_pics')
                .then((response) => {
                    this.data = response
                    console.log(response.status)
                })
            this.visible = false
        },
        delete_card(e) {
            this.$http.get('/Pics/delete_one_pic?id=' + e)
                .then((response) => {
                    this.data = response
                    console.log(response.status)
                    this.$http.get('/Pics/get_all_pics')
                        .then((response) => {
                            this.data = response
                            console.log(response.status)
                        })
                })
        },
        info () {
            this.$message.info('Build For TechMan By Sunbelife');
        },
        handleChange(info) {
            const status = info.file.status;
            if (status !== 'uploading') {
                console.log(info.file, info.fileList);
            }
            if (status === 'done') {
                this.$http.get('/Pics/get_all_pics')
                    .then((response) => {
                        this.data = response
                        console.log(response.status)
                    })
                this.$message.success(`${info.file.name} 文件上传完毕。`);
            } else if (status === 'error') {
                this.$message.error(`${info.file.name} 文件上传失败。`);
            }
        },
        go_to_show(){
            this.$router.push({path:'/show'})
        }
    },
}
